import Payrollmanagement.Manager;import Payrollmanagement.SalesRep;import Payrollmanagement.StaffMember;import java.util.Scanner;//TIP To <b>Run</b> code, press <shortcut actionId="Run"/> or// click the <icon src="AllIcons.Actions.Execute"/>// icon in the gutter.public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);    //Collect information        //SalesRep information        SalesRep[] sales  =  new SalesRep[5];        System.out.println("\n+++++++++++++++++++++++++++++++\n ");        for (int i = 0; i < 5; i++)        {            sales[i] = new SalesRep();            System.out.println("\tEnter Sales rep: " + (i+1));            // First Name Input User            System.out.print("\tFirst Name: ");            sales[i].setFirstname(scanner.next());            // Last Name Input User            System.out.print("\tLast Name: ");            sales[i].setLastname(scanner.next());            //Department Input User            System.out.print("\tDepartment Number: ");            sales[i].setDeptNumber(scanner.next());            // Working hour Input User            System.out.print("\tWorking Hours: ");            sales[i].setHoursWorked(scanner.nextDouble());            // Working hour Input User            System.out.print("\tAllowance: $");            sales[i].setAllowance(scanner.nextDouble());            System.out.println("_____________________________________\n ");        }        //Manger information        Manager manager1 = new Manager();        System.out.println("\n+++++++++++++++++++++++++++++++\n ");        System.out.println("\tEnter Manager Info: ");        // First Name Input User        System.out.print("\tFirst Name: ");        manager1.setFirstname(scanner.next());        // Last Name Input User        System.out.print("\tLast Name: ");        manager1.setLastname(scanner.next());        //Department Input User        System.out.print("\tDepartment Number: ");        manager1.setDeptNumber(scanner.next());        // Working hour Input User        System.out.print("\tWorking Hours: ");        manager1.setHoursWorked(scanner.nextDouble());        // Working hour Input User        System.out.print("\tBonus: $");        manager1.setBonus(scanner.nextDouble());        System.out.println("_____________________________________\n ");    //Display information        System.out.println("\n\n^^^^^^^^^^^^^^^^^^^^^^^^^^");        System.out.println("\tDisplay Info: ");        System.out.println("^^^^^^^^^^^^^^^^^^^^^^^^^^\n");        System.out.println("\tManager Info: ");        System.out.println("**************************\n");        manager1.display();        System.out.println("\tSale Rep Info: ");        System.out.println("**************************\n");        for (int j = 0; j < 5; j++)        {            sales[j].display();            System.out.println("+++++++++++++++++++++++++++++++\n ");        }        System.out.println("+++++++++++++++++++++++++++++++\n ");    }}